namespace wa_1235_jk_ecm_v4.CustomMiddleware
{
    public class SessionLoggingMiddleware
    {
        private readonly RequestDelegate _next;
        private readonly ILogger<SessionLoggingMiddleware> _logger;
        private readonly IGenericMethods _iGenericMethods;
        private readonly IAppSettingsService _appSettingsService;
        public static DecintellSettings? appSettings;
        public SessionLoggingMiddleware(RequestDelegate next, ILogger<SessionLoggingMiddleware> logger,
            IGenericMethods iGenericMethods, IAppSettingsService appSettingsService)
        {
            _next = next;
            _logger = logger;
            _iGenericMethods = iGenericMethods;
            _appSettingsService = appSettingsService;
            appSettings = _appSettingsService.GetAppSettings();
        }

        public async Task InvokeAsync(HttpContext context)
        {
            bool isLocal = context.Request.Host.Host.Equals("localhost", StringComparison.OrdinalIgnoreCase)
                         || context.Request.Host.Host.Equals("127.0.0.1", StringComparison.OrdinalIgnoreCase);            
            string AccessToken = context.Request.Cookies["1231_AccessToken"] ?? string.Empty;
            if (isLocal)
            {
                if (String.IsNullOrEmpty(AccessToken))
                {
                    await SetLocalHostCookies(context);
                    // Redirect to the same URL to ensure the cookie is available in the next request
                    context.Response.Redirect(context.Request.Path);
                    return; // Exit the method to wait for the next request
                }

            }
            if (!String.IsNullOrEmpty(AccessToken))
            {
                var sessionId = context.Session.GetInt32("iOemId").ToString();

                if (string.IsNullOrEmpty(sessionId))
                {
                    // _logger.LogInformation("<InvokeAsync>Resetting SessionKeys here....Session is available but no valid data found or session has expired");
                    await SetSessionKeys(context);
                }
                else
                {
                    //_logger.LogInformation($"<InvokeAsync>Session {sessionId} accessed at {DateTime.UtcNow}");
                }
            }

            else
            {
                _logger.LogInformation($"<InvokeAsync>Cookie not found at {DateTime.UtcNow}");
                // Set a cookie to trigger client-side logout and alert
                //context.Response.Cookies.Append("logoutAlert", "true", new CookieOptions { HttpOnly = false });
                // Redirect to a logout page or home page if cookie is not found
                context.Response.Redirect("/");
                await context.Response.CompleteAsync(); // Complete the response to halt further processing
                return; // Exit the method to prevent further execution
            }

            await _next(context);

        }

        private async Task SetLocalHostCookies(HttpContext context)
        {
            // Create cookie options
            var cookieOptions = new CookieOptions
            {
                // Set the cookie to expire after a specific duration (e.g., 1 hour)
                Expires = DateTime.Now.AddMinutes(120),//For local set 120 min
                                                       // Expires = DateTime.Now.AddMinutes(appSettings.CookieExpiryMin),//Set CookieExpiryMin in appseting.json
                                                       // Set the cookie to be accessible only over HTTPS
                Secure = true,

                // Restrict the cookie to a specific path
                Path = "/",

                // Set the domain if necessary
                // Domain = "yourdomain.com",

                // Set the cookie as HttpOnly to prevent client-side scripts from accessing it
                HttpOnly = false,
                //In this example, the SameSite attribute is set to SameSiteMode.None.
                //This means that the cookie will be sent with both cross-site and same-site requests. 
                // Set the SameSite attribute to control cross-site behavior
                //SameSiteMode.Strict: The cookie will only be sent in a first-party context,
                //and cross-site requests will not include the cookie.
                //SameSiteMode.Lax: The cookie will be sent with top - level navigations and same - site requests,
                //but not with cross-site requests initiated by third-party websites(e.g., links from external websites).
                SameSite = SameSiteMode.Lax
            };

            string userIp = context.Connection.RemoteIpAddress?.ToString();
            string userAgent = context.Request.Headers["User-Agent"];
            string apiEndPointtoken = $"jwt/generate-token?UserId={appSettings.UserId}&OemId={appSettings.OemId}&Culture={appSettings.Culture}&IpAddress={userIp}&UserAgent={Uri.EscapeDataString(userAgent)}";


            //var apiEndPointtoken = "jwt/generate-token?loginId=" + appSettings.UserId;
            //use this if testing with local api module or remove [Authorize] from api module while doing local development/testing
            // var apiEndPointtoken = "jwt/generate-token-localhost?loginId=" + appSettings.UserId; 
            var TokenDetail = JsonSerializer.Deserialize<TokenDetail>(await _iGenericMethods.GetDataJwt(apiEndPointtoken));
            context.Response.Cookies.Append("1231_AccessToken", TokenDetail.accessToken, cookieOptions);
        }

        private async Task SetSessionKeys(HttpContext context)
        {
            var apiEndPointgetinfo = "jwt/get-info";
            var data = JsonSerializer.Deserialize<JsonElement>(await _iGenericMethods.GetDataJwt(apiEndPointgetinfo));

            //GetUserInfo from Token
            // Make sure the properties exist before accessing them
            int OemId = data.TryGetProperty("oemId", out var oemId) ? int.Parse(oemId.GetString()) : 0;
            int UserId = data.TryGetProperty("userId", out var userId) ? int.Parse(userId.GetString()) : 0;
            string Culture = data.TryGetProperty("culture", out var strculture) ? strculture.GetString() : "";
            context.Session.SetString("Culture", Culture);

            // Retrieve ModuleId from the query string
            int ModuleId = 0;
            int.TryParse(context.Request.Query["ModuleId"], out ModuleId);
            if (ModuleId == 0)
            {
                ModuleId = appSettings.ModuleId;
            }

            context.Session.SetInt32("OemId", OemId);
            context.Session.SetInt32("CreatedBy", UserId);
            context.Session.SetInt32("iOemId", OemId);
            context.Session.SetInt32("iUserId", UserId);

            //now get relevant module menu
            string SiteURL = "https://" + context.Request.Host.Value + "/";
            var json = new
            {
                OemId,
                UserId,
                ModuleId,
                SiteURL
            };
            var menuData = JsonSerializer.Serialize(json);
            string apiEndPoint = "ModuleMenu/GetMenuTreeViewJson";
            var menulist = JsonSerializer.Deserialize<dynamic>(await _iGenericMethods.PostDataLogin(apiEndPoint, menuData));
            string menuDataJson = JsonSerializer.Serialize(menulist);
            context.Session.SetString("menuData", menuDataJson);

            apiEndPoint = "ModuleMenu/GetMenuTreeViewAllPageTypeDetailsJson";
            var menulistAllPage = JsonSerializer.Deserialize<dynamic>(await _iGenericMethods.PostDataLogin(apiEndPoint, menuData));
            string menulistAllPageJson = JsonSerializer.Serialize(menulistAllPage);
            context.Session.SetString("menuDataAllPage", menulistAllPageJson);  //commented by mayuri  3 jul 2024
                                                                                //  context.Session.SetString("menuDataAllPageAdmin", menulistAllPageJson); //added  by mayuri  3 jul 2024  "menuDataAllPageAdmin" key used all over the project.

            /////set other session information below this line
            ///
            await SetNotificationSessionKeys(context, ModuleId, OemId, UserId);
            await SetModuleSessionKeys(context, ModuleId, OemId, UserId);

            //Removed Global Variables from all code after bug reported on 22Aug2024 By Snehal
            //await SetGlobalVariables(context, OemId, UserId);
        }

        private async Task SetModuleSessionKeys(HttpContext context, int ModuleId, int OemId, int UserId)
        {
            string SiteURL = "https://" + context.Request.Host.Value + "/";
            var json = new
            {
                OemId,
                UserId,
                ModuleId,
                SiteURL
            };
            var menuData = JsonSerializer.Serialize(json);

            string apiEndPoint = "Masters/GetAdditionalUserDetail";
            dynamic[] UserDetail = JsonSerializer.Deserialize<dynamic[]>(await _iGenericMethods.PostDataAdmin(apiEndPoint, menuData));

            // Check if the array is not empty
            if (UserDetail.Length > 0)
            {
                JsonElement userElement = UserDetail[0];

                // Check if the properties exist before accessing them
                if (userElement.TryGetProperty("UserReportingManager", out var reportingManagerElement))
                {
                    GlobalVariables.Instance.ReportingManager = reportingManagerElement.GetInt32();
                }
                if (userElement.TryGetProperty("FinancialYear", out var reportingFinancialYear))
                {
                    GlobalVariables.Instance.FinancialYear = reportingFinancialYear.GetString();
                    context.Session.SetString("FinancialYear", reportingFinancialYear.GetString());
                }
                if (userElement.TryGetProperty("EmployeeGrade", out var employeeGradeElement))
                {
                    GlobalVariables.Instance.EmployeeGrade = employeeGradeElement.GetString();
                    context.Session.SetString("EmployeeGrade", employeeGradeElement.GetString());

                }
                if (userElement.TryGetProperty("EmployeeGradeName", out var employeeGradeElementName))
                {
                    GlobalVariables.Instance.EmployeeGradeName = employeeGradeElementName.GetString();
                    context.Session.SetString("EmployeeGrade", employeeGradeElementName.GetString());

                }
                if (userElement.TryGetProperty("DesignationName", out var userDesignationElement))
                {
                    GlobalVariables.Instance.RoleName = userDesignationElement.GetString();
                    context.Session.SetString("RoleName", userDesignationElement.GetString());

                }
                if (GlobalVariables.Instance.RoleName == "")
                {
                    GlobalVariables.Instance.RoleName = "0";
                }


            }
        }

        private async Task SetNotificationSessionKeys(HttpContext context, int ModuleId, int OemId, int UserId)
        {
            //now give call to Notification api and get details such as FirstName/LastName/Oem_Logos etc

            var jsonNotify = new
            {
                OemId,
                UserId,
                ModuleId
            };
            var notify_oem_logo = JsonSerializer.Serialize(jsonNotify);
            var apiEndPoint = "ModuleMenu/GetNotification_OemList_Logo";
            var Notification_OemList_Logo = JsonSerializer.Deserialize<List<Notification_OemList_Logo>>(await _iGenericMethods.PostDataLogin(apiEndPoint, notify_oem_logo));

            var OemList = Notification_OemList_Logo.First().OemList;

            var InitialLogo = OemList.Select(oem =>
            {
                var firstInitial = !string.IsNullOrEmpty(oem.UserFirstName) ? oem.UserFirstName[0].ToString().ToUpper() : string.Empty;
                var lastInitial = !string.IsNullOrEmpty(oem.UserLastName) ? oem.UserLastName[0].ToString().ToUpper() : string.Empty;
                return firstInitial + lastInitial;
            }).ToList();

            var singleInitialLogo = InitialLogo.FirstOrDefault();

            // Declare the base64Image variable globally
            string base64Image = null;

            // If there's no initial logo, we can return a placeholder image or handle as needed.
            if (!string.IsNullOrEmpty(singleInitialLogo))
            {
                // Create an image from the initials
                var width = 100;  // Set the width of the image
                var height = 100; // Set the height of the image
                var UserColor = Notification_OemList_Logo.First().UserHexColor;

                // Create a bitmap image
                using (Bitmap bitmap = new Bitmap(width, height))
                {
                    using (Graphics g = Graphics.FromImage(bitmap))
                    {
                        // g.Clear(Color.White); // Set the background color of the image

                        // Set up the font for the initials
                        using (Font font = new Font("Arial", 40, FontStyle.Bold, GraphicsUnit.Point))
                        {
                            // Set up the brush color for the text
                            using (Brush brush = new SolidBrush(ColorTranslator.FromHtml(UserColor)))
                            {
                                // Measure the size of the text to center it
                                var textSize = g.MeasureString(singleInitialLogo, font);

                                // Calculate the position to center the text
                                float x = (width - textSize.Width) / 2;
                                float y = (height - textSize.Height) / 2;

                                // Draw the text (initials) on the image
                                g.DrawString(singleInitialLogo, font, brush, new PointF(x, y));
                            }
                        }
                    }

                    // Optionally, convert the image to a base64 string if needed
                    using (MemoryStream ms = new MemoryStream())
                    {
                        bitmap.Save(ms, ImageFormat.Png);
                        base64Image = Convert.ToBase64String(ms.ToArray());
                    }
                }
            }

            // Now you can assign the base64Image to DefaultLogo variable
            string DefaultLogo = base64Image;

            var ModuleLogo = Notification_OemList_Logo?.First().LogoList?.First().ModuleLogo;
            var OEMLogo = Notification_OemList_Logo?.First().LogoList?.First().OEMLogo;

            var ApplicationUserProfilePic = Notification_OemList_Logo.First().ApplicationUserProfilePic;
            var NotFoundImg = "";
            //var DefaultImg = "src=\"../UI_Repository/dist/img/avatar4.png\"";
            var DefaultImg = "";
            if (ModuleLogo != null)
            {
                if (ModuleLogo != null)
                {
                    context.Session.SetString("FalcoLogo", ModuleLogo.ToString());
                }
                else
                {
                    context.Session.SetString("FalcoLogo", "data:image/png;base64," + NotFoundImg);
                }
                if (OEMLogo != null)
                {
                    // context.Session.SetString("OemImgLogo", "data:image/png;base64," + OEMLogo.ToString());
                    context.Session.SetString("OemImgLogo", OEMLogo.ToString());
                }
                else
                {
                    context.Session.SetString("OemImgLogo", "data:image/png;base64," + NotFoundImg);
                }

                if (!string.IsNullOrEmpty(ApplicationUserProfilePic))
                {
                    context.Session.SetString("UserImage", ApplicationUserProfilePic.ToString());
                }
                else
                {
                    context.Session.SetString("UserImage", "data:image/png;base64," + DefaultLogo);
                }


            }


            // Assuming Notification_OemList_Logo is a list or array of some type that contains a NotificationList property
            if (Notification_OemList_Logo != null && Notification_OemList_Logo.Count > 0 && Notification_OemList_Logo[0].NotificationList != null)
            {
                // Safe to serialize and count since we've checked it's not null
                var notificationListJson = JsonSerializer.Serialize(Notification_OemList_Logo[0].NotificationList);
                context.Session.SetString("NotificationList", notificationListJson);
                // Convert count to string safely
                context.Session.SetString("NotificationCount", Notification_OemList_Logo[0].NotificationCount[0].Notificationcnt.ToString());
            }
            else
            {
                // Handle the case where NotificationList is null or Notification_OemList_Logo is empty/null
                // For instance, you might want to store an empty JSON array and a count of 0
                context.Session.SetString("NotificationList", JsonSerializer.Serialize(new List<Notification_List>()));
                context.Session.SetString("NotificationCount", "0");
            }

            var oemListJson = JsonSerializer.Serialize(Notification_OemList_Logo[0].OemList);
            context.Session.SetString("OemList", oemListJson);
            //ViewBag.SelectedOEMID = OemId;//Check in javascript code and replace with session
            // Get the current year using DateTime
            context.Session.SetString("CurrentYear", DateTime.Now.Year.ToString());
            var FirstName = "Faster"; var LastName = "Fene";
            if (!string.IsNullOrEmpty(Notification_OemList_Logo[0].OemList[0].UserFirstName)) FirstName = Notification_OemList_Logo[0].OemList[0].UserFirstName;
            if (!string.IsNullOrEmpty(Notification_OemList_Logo[0].OemList[0].UserLastName)) LastName = Notification_OemList_Logo[0].OemList[0].UserLastName;
            context.Session.SetString("FirstName", FirstName);
            context.Session.SetString("LastName", LastName);
            context.Session.SetString("FullName", FirstName + " " + LastName);
            context.Session.SetString("EmployeeName", FirstName + " " + LastName);
            context.Session.SetString("Language", "en-US");
            context.Session.SetString("Locale", "en-In");
            context.Session.SetString("Currency", "INR");

        }

